import React from 'react';
import '../index.js';
import Header from '../components/Header';
import Footer from '../components/Footer';
import Main from '../components/Main';
import AddPlacePopup from '../components/AddPlacePopup';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import ImagePopup from '../components/ImagePopup';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import api from '../utils/Api';
import { Route, Switch, Redirect, useHistory } from 'react-router-dom';
import ProtectedRoute from './ProtectedRoute';
import Login from './Login';
import Register from './Register';
import InfoTooltip from './InfoTooltip';
import * as auth from '../utils/Auth';

function App() {

    const [isEditAvatarPopupOpen, setIsOpenPopupAvatar] = React.useState(false);
    const [isEditProfilePopupOpen, setIsOpenPopupProfile] = React.useState(false);
    const [isAddPlacePopupOpen, setIsOpenPopupPlace] = React.useState(false);
    const [selectedCard, setSelectedCard] = React.useState(false);
    const [imageData, setImageData] = React.useState(null)

    const [cards, setCards] = React.useState([])
    const [currentUser,  setCurrentUser] = React.useState(""); 

    const [loggedIn, setLoggedIn] = React.useState(false);
    const [isInfoTooltipOpen, setIsInfoTooltipOpen] = React.useState(false);
    const [email, setEmail] = React.useState('');
    const [isRequestSuccessful, setisRequestSuccessful] = React.useState(false);

    React.useEffect(() => {
        Promise.all([api.getUserInfoFromServer(), api.getCardsInformation()])
            .then(res => {
                console.log(res)
                setCurrentUser(res[0]);
                setCards(res[1]);
            })
            .catch((err) => console.error(err));
      }, []);

    function handleEditAvatarClick() {
        setIsOpenPopupAvatar(true);
    }

    function handleAddPlaceClick() {
        setIsOpenPopupPlace(true);
    }

    function handleEditProfileClick() {
        setIsOpenPopupProfile(true);
    }

    function handleCardLike(card) {
        // Снова проверяем, есть ли уже лайк на этой карточке
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        // Отправляем запрос в API и получаем обновлённые данные карточки
        api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
            // Формируем новый массив на основе имеющегося, подставляя в него новую карточку
          const newCards = cards.map((c) => c._id === card._id ? newCard : c);
          // Обновляем стейт
          setCards(newCards);
        })
        .catch((err) => console.error(err));
    } 

    function handleCardDelete(card) {
        // Отправляем запрос в API и получаем обновлённые данные карточки
        api.deleteCard(card._id)
            .then(() => {
                setCards(cards.filter(c => c._id !== card._id));
        })
        .catch((err) => console.error(err));
    }

    function handleUpdateUser(userData) {
        api.setUserInfoOnServer(userData.name, userData.about)
            .then(res => {
                setCurrentUser(res);
                closeAllPopups();
                closeAllPopups()
        })
        .catch((err) => console.error(err))
    }

    function handleUpdateAvatar(userData) {
        api.editProfileAvatar(userData.avatar)
            .then(res => {
                setCurrentUser(res);
                closeAllPopups()
        })
        .catch((err) => console.error(err))

    }

    function handleAddPlaceSubmit(cardData) {
        // console.log(cards);
        api.addCards(cardData.name, cardData.link)
            .then(card => {
                console.log(cards)
                setCards([card, ...cards]);
                closeAllPopups()
        })
        .catch((err) => console.error(err))
    }

    function handleCardClick(card) {        
        setSelectedCard(true);
        setImageData(card);
    }

    function closeAllPopups() {
        setIsOpenPopupAvatar(false);
        setIsOpenPopupProfile(false);
        setIsOpenPopupPlace(false);
        setSelectedCard(null);
    }

    function handleRegister(email, password) {
        auth.register(email, password)
        .then((res) => {
          if (res) {
            setIsInfoTooltipOpen(true)
            setisRequestSuccessful(true)
            history.push('/sign-in')
          }
        })
        .catch((err) => {
          console.log(err)
          setIsInfoTooltipOpen(true)
          setisRequestSuccessful(false)
        })
    }

    function handleLogin(email, password) {
        auth.authorize(email, password)
        .then((res) => {
          if (res.token){
              localStorage.setItem('jwt', res.token)
              return res
          } else {
              return
          }
    })
        .then(data => {
          if (data.token) {
            setLoggedIn(true)
            setEmail(email)
            history.push('/')
          }
        })
        .catch(err => {
          console.log(err)
          setIsInfoTooltipOpen(true)
          setisRequestSuccessful(false)
        })
    }

      function handleLogout() {
        setLoggedIn(false)
        setEmail(email)
        localStorage.removeItem('jwt')
        history.push('/sign-in')
    }

    return (
        <div className="page">
            <CurrentUserContext.Provider value={currentUser}>
                <Header 
                    email={email} 
                    loggedOut={handleLogout} 
                    loggedIn={loggedIn}
                />  
                <Switch>
                <ProtectedRoute exact path='/' loggedIn={loggedIn}>
                    <Main
                        onEditAvatar={handleEditAvatarClick}
                        onEditProfile={handleEditProfileClick}
                        onAddPlace={handleAddPlaceClick}
                        onCardClick={handleCardClick}
                        onCardList={cards}
                        onCardLike={handleCardLike}
                        onDeleteCard={handleCardDelete}
                    />  
                    <Footer />
                </ProtectedRoute>        
                <Route path='/sign-in'>
                    <Login handleLogin={handleLogin} setEmail={setEmail} />
                </Route>
                <Route path='/sign-up'>
                    <Register handleRegister={handleRegister} />
                </Route>
                <Route>
                    {loggedIn ? <Redirect to='/' /> : <Redirect to='/sign-in' />}
                </Route>
                </Switch>  


                <EditProfilePopup 
                    isOpen={isEditProfilePopupOpen} 
                    onClose={closeAllPopups} 
                    onUpdateUser={handleUpdateUser}
                /> 

                <EditAvatarPopup 
                    isOpen={isEditAvatarPopupOpen} 
                    onClose={closeAllPopups} 
                    onUpdateAvatar={handleUpdateAvatar}
                /> 

                <AddPlacePopup
                    isOpen={isAddPlacePopupOpen}
                    onClose={closeAllPopups}
                    onAddCard={handleAddPlaceSubmit}
                />

                <ImagePopup
                    card={imageData}
                    isOpen={selectedCard}
                    onClose={closeAllPopups}
                />

                <InfoTooltip isRequestSuccessful={isRequestSuccessful} isOpen={isInfoTooltipOpen} onClose={closeAllPopups} />


            </CurrentUserContext.Provider>
        </div>
    );
}

export default App;
