import React from 'react';
import { withRouter } from 'react-router-dom';

function Login({handleLogin}) {
    // const [password, setPassword] = React.useState('')
    // const [email, setEmail] = React.useState('')

    // function handleSubmit(event) {
    //     event.preventDefault()
    //     if (!email || !password){
    //         return
    //     }
    //     props.handleLogin(email, password)
    // }

    // function handleChangeEmail(evt) {
    //     setEmail(evt.target.value)
    // }

    // function handleChangePassword(evt) {
    //     setPassword(evt.target.value)
    // }

    const initialData = {
        email: '',
        password: '',
      }

      const [profileData, setProfileData] = React.useState(initialData);

      const handleChange = (e) => {
        const { name, value } = e.target
        setProfileData(profileData => ({
          ...profileData,
          [name]: value
        }));
      };

      const handleSubmit = (e) => {
        e.preventDefault()

        if (!profileData.password || !profileData.email) {
          return;
        }

        handleLogin(profileData)
      }

    return (
        <>
        <div className='register'>
            <h2 className='register__title'>Вход</h2>
            <form className='register__form' onSubmit={handleSubmit} >
                <input className='register__input' placeholder='email' type='email' required minLength='2' maxLength='40' onChange={handleChange} />
                <input className='register__input' placeholder='Пароль' type='password' required minLength='2' maxLength='40' onChange={handleChange} />
                <button type='submit' className='register__button-save' onSubmit={handleSubmit}>Войти</button>
            </form>
        </div>
        </>
    )
}

export default withRouter(Login);
